'use strict';window.onload=function(){startClock();document.getElementById("add").onclick=function(){var a=document.getElementById("input"),a=parse(a);write(a)};document.getElementById("reset").onclick=function(){document.getElementById("clock").value="00:00:00"}};function parse(a){return a}function write(a,c){var b=document.createElement("li"),d=document.getElementById("output");b.appendChild(document.createTextNode(a));b.setAttribute("id",c);d.appendChild(b)}
function startClock(){var a=new Date,c=a.getHours(),b=a.getMinutes(),a=a.getSeconds(),b=checkTime(b),a=checkTime(a);document.getElementById("clock").value=c+":"+b+":"+a;setTimeout(startClock,500)}function checkTime(a){10>a&&(a="0"+a);return a};
