'use strict';window.onload=function(){document.getElementById("start").onclick=function(){var a=document.getElementById("input").value,b=document.querySelector("input[name ='resultType']:checked").value,a=convert(a,b);write(a,b)}};function convert(a,b){switch(b){case "binary":return binary(a);case "decimal":return decimal(a);default:return"error"}}function binary(a){var b=a+" => ",c=1;do b+=a%2,c=a/=2;while(0<c);return[b.toString(),"green"]}function decimal(a){return["DECIMAL: "+a,"blue"]}
function write(a,b){var c=document.createElement("li"),d=document.getElementById("output");c.appendChild(document.createTextNode(a));c.setAttribute("id",b);d.appendChild(c)};
