'use strict';window.onload=function(){document.getElementById("run").onclick=function(){var a=document.getElementById("input").value;write(reverse(a.toLowerCase()))}};function reverse(a){for(var b=Array.from(a),c=[];b[0];)c.push(b.shift());return a+" => "+c.join("")}function write(a){var b=document.createElement("li"),c=document.getElementById("output");b.appendChild(document.createTextNode(a));c.appendChild(b)};
